package mars.rover.kotlin.app

class Mars constructor(x: Int, y: Int){
    var position = Coordinate(x, y)
    var direction = "South"
    fun forward(){
        position.x += 1
    }
    fun backward(){
        position.y -= 1
    }
    fun left(){
        if (direction == "South"){
            direction = "East"
        } else if (direction == "East"){
            direction = "North"
        } else if (direction == "North"){
            direction = "West"
        } else direction = "South"
    }
    fun right(){
        if (direction == "South"){
            direction = "West"
        } else if (direction == "West"){
            direction = "North"
        } else if (direction == "North"){
            direction = "East"
        } else direction = "South"
    }
}

fun main(){
    val mars = Mars(0, 0) //creame un objeto a partir de esta clase
    val mars2 = Mars(1, 3) // otro objeto
    mars.backward()
    mars2.backward()
    mars2.backward()
    println(mars.position)
    println(mars2.position)

}

class Coordinate(x: Int, y: Int) {
    var x = x
    var y = y
   override fun toString(): String{
       return "${x}, ${y}"
    }
}